INSERT INTO hotels (name, country_id, rating, description) VALUES
    ('Отель Астория', 1, 4.2, 'Прекрасный отель в центре города'),
    ('Гранд Отель', 2, 3.8, 'Уютный отель недалеко от моря'),
    ('Отель Эмеральд', 3, 4.5, 'Роскошный отель с видом на горы'),
    ('Отель Хайатт', 1, 3.5, 'Удобное расположение в историческом районе'),
    ('Отель Савой', 4, 4.0, 'Современный отель на побережье'),
    ('Отель Ритц', 5, 3.7, 'Уединенный отель в горной местности'),
    ('Золотое Кольцо', 1, 3.3, 'Расположенный в самом сердце исторического Золотого Кольца России, отель "Золотое Кольцо" предлагает уникальную возможность погрузиться в богатое культурное наследие страны. Гости могут наслаждаться комфортабельными номерами с элементами традиционного дизайна, рестораном, где подаются блюда русской кухни, и прекрасными видами на исторические достопримечательности.'),
    ('Байкал', 1, 4.7, 'Расположенный на берегу озера Байкал, отель "Байкал" предлагает уединенный отдых в окружении природной красоты. Гости могут расслабиться в уютных номерах с панорамными видами на озеро, насладиться местной кухней в ресторане и заняться различными активностями на открытом воздухе, такими как пешие прогулки, лодочные прогулки и посещение национального парка Байкал.'),
    ('Санте', 1, 4.0, 'Погрузитесь в мир роскоши в отеле "Санте", расположенном в самом сердце Санкт-Петербурга. Этот роскошный отель предлагает роскошные номера с элегантным декором, ресторан с изысканной международной кухней и спа-центр, где гости могут насладиться оздоровительными процедурами. Удобное расположение отеля позволяет легко добраться до исторических достопримечательностей города, включая Эрмитаж и Петропавловскую крепость.'),
    ('Хилтон', 1, 3.8, 'Расположенный в самом сердце исторического города Казань, отель "Хилтон" предлагает непревзойденный комфорт и гостеприимство. Гости могут наслаждаться роскошными номерами с современным дизайном и удобствами, рестораном, где подаются блюда татарской и международной кухни, а также спа-салоном, где они могут расслабиться и оздоровиться. Отель "Казанская жемчужина" также предлагает удобное расположение, позволяющее гостям легко добраться до главных достопримечательностей города, включая Казанский Кремль, Кули Шариф и Булгарский монастырь. Этот отель является идеальным местом для тех, кто желает испытать великолепие и богатство культуры Казани.'),
    ('Золотой Акрополь', 5, 5.0, 'Отель "Золотой Акрополь" расположен в самом сердце Афин, всего в нескольких минутах ходьбы от Акрополя и других исторических достопримечательностей. Гости могут насладиться роскошными номерами с видом на город, фитнес-центром, спа-салоном и рестораном, где подаются изысканные блюда средиземноморской кухни.'),
    ('Олимпийские сокровища', 5, 3.9, 'Расположенный рядом с древними руинами Олимпии, отель "Олимпийские сокровища" предлагает уникальную возможность погрузиться в историю Греции. Гости могут остановиться в уютных номерах, окруженных зелеными садами, и насладиться рестораном с видом на археологический комплекс. Отель также предлагает бассейн и спа-салон для полного расслабления.'),
    ('Пляжный курорт', 2, 4.3, 'Отель с бассейном и сауной'),
    ('Отель Эдельвейс', 3, 4.1, 'Отель с рестораном и фитнес-центром'),
    ('Отель Берлин', 4, 3.9, 'Отель с красивым садом и прудом'),
    ('Отель Шанти', 4, 5, 'Роскошный отель с панорамным видом на океан, прекрасными номерами и первоклассными удобствами. Идеальное место для роскошного отдыха и релаксации.'),
    ('Отель Виктория', 4, 3, 'Уникальный отель в центре города с великолепными видами на городскую силуэт и современный дизайн интерьеров. Идеальное место для деловых путешественников и любителей роскоши.'),
    ('Отель 4 сезона', 4, 4.1, 'Роскошный пляжный отель с белоснежным песком, теплым солнцем и кристально чистой водой. Удобства и развлечения для всей семьи, чтобы насладиться идеальным пляжным отдыхом.'),
    ('Оазис Спрингс', 4, 4.0, 'Уединенный курорт с прекрасным спа-центром, где вы сможете отдохнуть, восстановить силы и насладиться разнообразными спа-процедурами. Идеальное место для релаксации и отдыха.'),
    ('Виллоу Крик', 4, 4.5, 'Роскошный горный отель с великолепными видами на горы, уютными номерами и возможностью заняться экстремальными видами спорта. Идеальное место для активного отдыха и исследования природы.'),
    ('Лагуна Бриз', 5, 4.8, 'Уютный отель на берегу лагуны с чистыми пляжами, теплым морем и расслабляющей атмосферой. Насладитесь спокойствием и красотой природы в этом уединенном уголке рая.'),
    ('Отель Эспланада', 4, 4.4, 'Отель с шикарным спа-центром и рестораном'),
    ('Ле Шато дАмур', 2, 3.8, 'Отель "Ле Шато дАмур" расположен в питторескной деревне в сердце французской провинции. Этот уютный отель предлагает гостям уникальный опыт пребывания в исторической здании, наполненном атмосферой романтики и очарования. Номера отеля оформлены в традиционном французском стиле, с элементами деревянной мебели и декоративных узоров. Гости могут насладиться утонченной французской кухней в ресторане отеля, где подаются блюда из свежих местных продуктов. Отель "Ле Шато дАмур" окружен живописными пейзажами, виноградниками и средневековыми замками, создавая идеальную атмосферу для романтических прогулок и отдыха вдали от городской суеты.'),
    ('Ле Гранд Эскурьяль', 2, 4.4, 'Расположенный в самом сердце Парижа, отель "Ле Гранд Эскурьяль" предлагает гостям непревзойденный комфорт и роскошь. Этот пятизвездочный отель воплощает элегантность и изысканность французского стиля, предоставляя роскошные номера с просторными интерьерами и современными удобствами. Гости могут наслаждаться высококлассным обслуживанием, отведать изысканные блюда французской кухни в ресторане отеля и расслабиться в спа-салоне, где им предложат широкий спектр процедур для релаксации и оздоровления. Отель "Ле Гранд Эскурьяль" идеально расположен в непосредственной близости от известных достопримечательностей, таких как Эйфелева башня и Лувр, делая его прекрасным выбором для тех, кто хочет насладиться французским шармом и роскошью.'),
    ('Бель Этуаль', 2, 4.0, 'Расположенный на берегу побережья Канн, отель "Бель Этуаль" предлагает своим гостям роскошное проживание в окружении прекрасных пейзажей и средиземноморского очарования. Этот элегантный отель предлагает просторные номера и люксы с панорамными видами на море, а также современные удобства и высококлассное обслуживание. Гости могут наслаждаться отличным пляжем, частным бассейном и спа-салоном, где им предложат широкий спектр процедур для релаксации и оздоровления. Ресторан отеля предлагает изысканные блюда средиземноморской кухни, приготовленные из свежих местных продуктов. Отель "Бель Этуаль" - идеальное место для тех, кто хочет насладиться роскошью и красотой Лазурного побережья Франции.'),
    ('Ле Винтаж Бордо', 2, 4.3, 'Расположенный в самом сердце прекрасного города Бордо, отель "Ле Винтаж Бордо" представляет собой уникальное сочетание элегантности и винного наследия. Этот отель, оформленный в стиле ретро, предлагает своим гостям уютные номера с винтажным декором и современными удобствами. Гости могут насладиться изысканными винами в винном баре отеля, где предлагается широкий выбор лучших местных и международных вин. Ресторан отеля приглашает гостей насладиться утонченными блюдами французской кухни, приготовленными из свежих местных продуктов. Отель "Ле Винтаж Бордо" идеально расположен в непосредственной близости от известных винодельческих регионов Бордо, делая его прекрасным местом для тех, кто хочет погрузиться в мир вин и насладиться богатством культуры этого региона.'),
    ('Ля Променад Марсель', 2, 3.9, 'Отель "Ля Променад Марсель" находится в самом сердце живописного города Марсель, предлагая гостям уникальную атмосферу Средиземноморской ривьеры. Этот современный отель идеально сочетает стиль и комфорт, предлагая своим гостям просторные номера с современным дизайном и удобствами. Гости могут насладиться потрясающими видами на море и город с террасы отеля, а также отведать изысканные блюда средиземноморской кухни в ресторане отеля. Отель "Ля Променад Марсель" также предлагает доступ к роскошному спа-салону, где гости могут расслабиться и оздоровиться с помощью разнообразных процедур и массажей. Расположенный в непосредственной близости от пляжей, исторических достопримечательностей и модных магазинов, отель "Ля Променад Марсель" является идеальным местом для тех, кто хочет насладиться Марселем во всей его красе и роскоши.'),
    ('Ла Прованс Сюит', 2, 4.7, 'Отель "Ла Прованс Сюит" представляет собой уютный и шикарный отель, расположенный в живописном регионе Прованс во Франции. Этот отель предлагает своим гостям просторные и стильно оформленные люксы, в которых они могут наслаждаться приватностью и комфортом. Ресторан отеля предлагает изысканные блюда местной и средиземноморской кухни, приготовленные из свежих местных продуктов. Гости могут также расслабиться у бассейна, окруженного садом, или отправиться на прогулку по захватывающим пейзажам Прованса. Отель "Ла Прованс Сюит" является идеальным местом для тех, кто хочет насладиться уникальной атмосферой и красотой Прованса в роскоши и уюте.'),
    ('Ил Белло Рипосо', 3, 4.5, 'Расположенный в сердце Тосканы, отель "Ил Белло Рипосо" обещает гостям спокойный и уютный отдых. Аутентичные номера и люксы отеля предлагают прекрасные виды на окружающие холмы и виноградники. Гости могут насладиться вкусами итальянской кухни в ресторане отеля или провести время на прогулках в саду и близлежащих туристических маршрутах.'),
    ('Ла Каса Белла', 3, 4.2, 'Отель "Ла Каса Белла" расположен в очаровательном городе Флоренция и предлагает гостям уникальный опыт проживания в старинном особняке. Антикварная мебель и оригинальные элементы декора создают атмосферу роскоши и романтики. Отель находится в непосредственной близости от исторических достопримечательностей города, а гости могут насладиться итальянскими деликатесами в ресторанах и кафе поблизости.'),
    ('Ил Палацио Магнифико', 3, 3.5, 'Отель "Ил Палацио Магнифико" представляет собой роскошное итальянское поместье, расположенное на берегу озера Комо. Изысканные номера и люксы отеля оформлены в классическом стиле с элементами итальянского шарма. Гости могут насладиться прогулками по садам отеля, наслаждаться прекрасными видами на озеро и расслабиться в спа-центре, предлагающем различные процедуры и массажи.'),
    ('Ил Маре Адриатико', 3, 4.1, 'Расположенный на побережье Адриатического моря, отель "Ил Маре Адриатико" предлагает гостям роскошные номера с видом на море и прекрасные пляжи. Отель предлагает различные водные развлечения, такие как серфинг и виндсерфинг, а также рестораны, специализирующиеся на свежих морепродуктах. Гости могут насладиться спокойными прогулками по пляжу и наслаждаться красотой Адриатического побережья.'),
    ('Метеора Вью', 5, 5.0, 'Расположенный в районе Метеора, отель "Метеора Вью" предлагает великолепные виды на знаменитые монастыри, возвышающиеся на скалах. Этот уникальный отель предлагает роскошные номера и апартаменты в традиционном греческом стиле. Гости могут насладиться террасой отеля, откуда открывается захватывающий вид, и насладиться тишиной и спокойствием этого уникального места. Отель "Метеора Вью" является идеальным местом для путешественников, желающих исследовать красоту Метеор и насладиться природным великолепием Греции.'),
    ('Санторини Палас', 5, 4.4, 'Расположенный на острове Санторини, отель "Санторини Палас" предлагает незабываемое проживание в одном из самых красивых мест в Греции. Этот роскошный отель предлагает стильные номера и виллы с видом на Эгейское море и знаменитый кратер. Гости могут насладиться роскошным бассейном, спа-салоном и ресторанами, предлагающими изысканные греческие и международные блюда. Отель "Санторини Палас" предлагает непревзойденное сочетание роскоши, красоты и уникального атмосферного опыта на острове Санторини.'),
    ('Оазис Крит', 5, 4.3, 'Расположенный на острове Крит, отель "Критская Оазис" предлагает уединенное проживание в окружении прекрасных садов и бирюзового моря. Этот эксклюзивный отель предлагает стильные номера и виллы с просторными балконами или террасами. Гости могут насладиться частным пляжем, спа-центром и ресторанами с изысканными блюдами. Отель "Критская Оазис" является идеальным местом для романтического отдыха или спокойного семейного времяпрепровождения.'),
    ('Роман Резорт', 5, 3.4, 'Расположенный в сердце Рима, отель "Роман Резорт" предлагает идеальное сочетание пляжного отдыха и природной красоты. Этот современный отель предлагает комфортабельные номера и виллы с видом на море или сады. Гости могут насладиться прямым доступом к песчаному пляжу, бассейнами, ресторанами и развлекательными мероприятиями. Отель "Роман Резорт" является отличным выбором для тех, кто ищет комфортный и разнообразный отдых.');
